  select  LU_KEY,CLASS_TYPE,ORDER_LINE_NUMBER,OWNER_ID,PRODUCT_ID,QUANTITY,TU_ID,TU_TYPE,UOM_ID,ASN_LINE_NUMBER,ASN_TU_ID,LAST_CYCLE_COUNT_DATE,PREV_PRODUCT_OWNER,PREV_PRODUCT_ID,PREV_QUANTITY,REASON_CODE,REASON_TEXT,SUSPECT,X_POSITION,Y_POSITION,Z_POSITION,INVENTORY_STATUS_KEY,INVENTORY_RECONCILIATION_KEY,ORDER_TU_PICK_KEY
